mapper.insert:
INSERT INTO board (
board_no, emp_no, board_title, board_cn, board_reg_dt, board_mdfcn_dt,
board_rdcnt, imprtnc_tag_yn, del_yn, file_group_no, board_ty, cvpl_ty,
cvpl_sttus
) VALUES (
SEQ_BOARD.NEXTVAL, #{empNo}, #{boardTitle}, #{boardCn}, SYSDATE, SYSDATE, 0,
#{imprtncTagYn}, 'N', #{fileGroupNo}, #{boardTy}, #{cvplTy},
#{cvplSttus}
)

mapper.select:
SELECT a.* FROM ( SELECT ROW_NUMBER() OVER ( ORDER BY b.board_mdfcn_dt DESC ) rnum, b.board_no, b.emp_no, b.board_title, b.board_cn, b.board_reg_dt, b.board_mdfcn_dt, b.board_rdcnt, b.imprtnc_tag_yn, b.del_yn, b.file_group_no, b.board_ty, st.cmmn_cd_nm as cvpl_ty, ty.cmmn_cd_nm as cvpl_sttus, e.emp_nm FROM board b INNER JOIN emp e ON b.emp_no = e.emp_no INNER JOIN cmmn_cd st on st.cmmn_cd_id = b.cvpl_ty INNER JOIN cmmn_cd ty on ty.cmmn_cd_id = b.cvpl_sttus WHERE b.del_yn = 'N' AND b.board_ty = '02003'
) a
WHERE a.rnum BETWEEN #{startRow} AND #{endRow}

mapper.update:
UPDATE files
SET DEL_YN = 'Y'
WHERE file_no = #{fileNo}
