mapper.select:
SELECT FILE_NO, ff.EMP_NO, ff.FOLDER_NO, ORIGINAL_NM, SAVED_NAME, FILE_PATH, FILE_SIZE, FILE_FANCYSIZE, FILE_MIME, FILE_REG_DT, ff.DEL_YN FROM FOLDER_FILE ff INNER JOIN FOLDERS f on (f.folder_no = ff.folder_no) WHERE ff.EMP_NO = #{empNo}
AND 1 = 0
AND (f.folder_ty = '10001' OR ff.folder_no = 1) AND ff.DEL_YN = 'N'
AND ff.FOLDER_NO = #{folderNo}

mapper.update:
UPDATE FOLDER_FILE
SET
    FOLDER_NO = #{targetFolderNo}
WHERE
    FILE_NO = #{fileNo}

UPDATE FOLDERS
SET
    DEL_YN = #{delYn}
WHERE
    FOLDER_NO = #{folderNo}

UPDATE FOLDER_FILE
SET
    DEL_YN = #{delYn}
WHERE
    FOLDER_NO = #{folderNo}

UPDATE FOLDER_FILE
SET
    DEL_YN = #{delYn}
WHERE
    FILE_NO = #{fileNo}

UPDATE FOLDERS
   SET DEL_YN = #{delYn}
 WHERE FOLDER_NO IN (
        SELECT FOLDER_NO
          FROM FOLDERS
         START WITH FOLDER_NO = #{folderNo}
         CONNECT BY PRIOR FOLDER_NO = UPPER_FOLDER
      )

UPDATE FOLDER_FILE
   SET DEL_YN = #{delYn}
 WHERE FOLDER_NO IN (
        SELECT FOLDER_NO
          FROM FOLDERS
         START WITH FOLDER_NO = #{folderNo}
         CONNECT BY PRIOR FOLDER_NO = UPPER_FOLDER
      )
